import React from 'react';
import './App.css';
import {observer,inject} from 'mobx-react'

@inject('TodoStore')
@observer
class App extends React.Component {

  handleSubmit = (e)=>{
    e.preventDefault();
    console.log(this.task.value);
    this.props.TodoStore.addTodo(this.task.value);
    this.task.value='';
  }

  handleChange = (e) => {
    console.log(e);
  }

  render(){
    const store = this.props.TodoStore;
  return (
    <div className="App">
      <h1>Todo List</h1>
      <h2>{store.getTodoCount} pending todos</h2>
      <form onSubmit={this.handleSubmit}>
        <input type="text" placeholder="add new todo" ref={inp => this.task=inp}/>
        <button>Add todo</button>
      </form>
      <ul>
        {
          store.todos.map((todo,idx)=>(<TodoView todo={todo} key={key}/>))
        }
      </ul>
    </div>
  );
}
}

class TodoView extends React.Component{
  render(){
    const todo = this.props.todo;
    const key = this.props.key;
    return(
      <li key={key}>

{todo.task} -> <small>{todo.done ? "finished" :"pending"}</small>
      </li>
    )
  }
}
export default App;
